-- trigger pentru update automat de stoc
CREATE OR REPLACE FUNCTION update_stock()
    RETURNS TRIGGER
    LANGUAGE PLPGSQL
AS
$$
BEGIN
    IF NEW.size = 'XS' THEN
        UPDATE STOCK
        SET XS = XS - NEW.pieces
        WHERE product_id = NEW.product_id;
    ELSIF NEW.size = 'S' THEN
        UPDATE STOCK
        SET S = S - NEW.pieces
        WHERE product_id = NEW.product_id;
    ELSIF NEW.size = 'M' THEN
        UPDATE STOCK
        SET M = M - NEW.pieces
        WHERE product_id = NEW.product_id;
    ELSIF NEW.size = 'L' THEN
        UPDATE STOCK
        SET L = L - NEW.pieces
        WHERE product_id = NEW.product_id;
    ELSE
        UPDATE STOCK
        SET XL = XL - NEW.pieces
        WHERE product_id = NEW.product_id;
    END IF;

    RETURN NEW;
END;
$$;

CREATE TRIGGER update_stock_trigger
    AFTER UPDATE OF order_id
    ON CART
    FOR EACH ROW
EXECUTE PROCEDURE update_stock();